<?php require_once(realpath(dirname(__FILE__).'/lib/log4jphp/Logger.php'));Logger::configure(realpath(dirname(__FILE__).'/../conf/log4php.properties'));require_once('class.Conf.php');require_once('class.Language.php');require_once('class.Message.php');// Some parameters used a lot$lang = Language::$lang;$baseUri = Conf::get('base_uri');// Convenient shortcuts to Language::lang($str) and Language::_lang($str)function lang($str) {	return call_user_func_array('Language::lang', func_get_args());}function _lang($str) {	print call_user_func_array('Language::lang', func_get_args());}